import React, { useState } from 'react';
import { signInWithPopup, GoogleAuthProvider, signOut } from "firebase/auth";
import { getAuth, createUserWithEmailAndPassword } from "firebase/auth";
import { signInWithEmailAndPassword } from "firebase/auth";
import { fireAuth } from "./firebase";

const LoginForm: React.FC = () => {
  const [userName, setUserName] = useState<string | null>(null);
  const [email, setEmail] = useState(0);
  const [password, setPassword] = useState("");
  //メール・パスワードでログインする
  const auth = getAuth();
  createUserWithEmailAndPassword(auth, email, password)
  .then((userCredential) => {
    // Signed in 
    const user = userCredential.user;
    alert("ログインユーザー: " + user.displayName);
    setUserName(user.displayName)
  })
  .catch((error) => {
    const errorMessage = error.message;
    alert(errorMessage);
  });

  const signInWithPassword = getAuth();
  signInWithEmailAndPassword(signInWithPassword, email, password)
  .then((userCredential) => {
    // Signed in 
    const user = userCredential.user;
    alert("ログインユーザー: " + user.displayName);
    setUserName(user.displayName);
  })
  .catch((error) => {
    const errorMessage = error.message;
    alert(errorMessage);
  });


  /**
   * googleでログインする
   */
  const signInWithGoogle = (): void => {
    // Google認証プロバイダを利用する
    const provider = new GoogleAuthProvider();


    // ログイン用のポップアップを表示
    signInWithPopup(fireAuth, provider)
      .then(res => {
        const user = res.user;
        alert("ログインユーザー: " + user.displayName);
        setUserName(user.displayName)
      })
      .catch(err => {
        const errorMessage = err.message;
        alert(errorMessage);
      });
  };

  /**
   * ログアウトする
   */
  const signOutWithGoogle = (): void => {
    signOut(fireAuth).then(() => {
      alert("ログアウトしました");
    }).catch(err => {
      alert(err);
    });
  };

  const handleSubmit = async (e: { preventDefault: () => void; }) => {
    e.preventDefault()
    const response = await fetch(
      "https://test2-9881e-default-rtdb.firebaseio.com/.json",
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      }
    );
  };

  return (
    <div>
      <form style={{ display: "flex", flexDirection: "column" }} onSubmit={handleSubmit}>
            <label>Email: </label>
            <input
              type="number"
              value={email}
              onChange={(e) => setEmail(parseInt(e.target.value))}
            ></input>
      </form>
      <form style={{ display: "flex", flexDirection: "column" }} onSubmit={handleSubmit}>
            <label>Password: </label>
            <input
              type="number"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            ></input>
            <button type={"submit"}>Submit</button>
      </form>
      {
        userName !== null ?
          <div>
            hello, {userName}
          </div> : <></>
      }
    </div>
  );
};

export default LoginForm;
